require "pry"
# dabAcCaCBAcCcaDA  The first 'cC' is removed.
# dabAaCBAcCcaDA    This creates 'Aa', which is removed.
# dabCBAcCcaDA      Either 'cC' or 'Cc' are removed (the result is the same).
# dabCBAcaDA        No further actions can be taken.
test = "dabAcCaCBAcCcaDA"

input = ""

class Polymer

  attr_accessor :chain, :last_removed

  def initialize(chain)
    @chain = chain
    @last_removed = 0
  end

  def compact
    removed_reactions = true
    while removed_reactions
      removed_reactions = self.remove_reaction
    end
    self.chain.length
  end

  def remove_reaction
    (self.last_removed...self.chain.length).each do |i|
      if i != self.chain.length - 1 && (self.chain[i] == self.chain[i+1].upcase || self.chain[i] == self.chain[i+1].downcase) && self.chain[i] != self.chain[i+1]
        self.chain[i..i+1] = ""
        self.last_removed = i > 0 ? i - 1 : 0
        return true
      end
    end
    return false
  end


  def length_of_minimal_compact
    counter = ("a".."z").each_with_object({}) do |el, counter|
      updated = self.chain.gsub(/#{el}|#{el.upcase}/, "")
      if updated.include?(el) || updated.include?(el.upcase)
        binding.pry
      end
      poly = Polymer.new(updated)
      poly.compact
      counter[el] = poly.chain.length
    end
    counter.min_by {|el, val| val}.last
  end

end

def run(str)
  puts "ANSWER 1: #{Polymer.new(str).compact}"
  puts "ANSER 2: #{Polymer.new(str).length_of_minimal_compact}"
end

run(input)
